let nav=document.querySelector(".header__nav"),burgerBtn=document.querySelector(".header__nav-burger");function navToggle(){nav.classList.contains("header__nav--closed")?(nav.classList.remove("header__nav--closed"),nav.classList.add("header__nav--opened")):(nav.classList.remove("header__nav--opened"),nav.classList.add("header__nav--closed"))}burgerBtn.onclick=navToggle;